import {Formik} from "formik";
import classes from "./Footer.module.scss"
import * as yup from "yup";

function FormSubscribe () {
    const validationSchema = yup.object().shape({
        email: yup.string().email('Invalid email').typeError('must be string').required('Enter email'),
    })


    return(
        <div>
            <Formik
                initialValues = {{
                    email: " "
            }}
                validateOnBlur
                onSubmit={(values,{resetForm}) => {
                console.log(values);
                resetForm();

            }}
                validationSchema={validationSchema}
            />
            {({ values, errors, touched, handleChange, handleBlur, isValid, handleSubmit, dirty }) => (
                <p>
                    <label className={classes.footer_label_subscribe} htmlFor={"email"}>Имя</label><br />
                    <input
                        className={classes.footer_input_subscribe}
                        type={"email"}
                        name={"email"}
                        onChange={handleChange}
                        onBlur={handleBlur}
                        value={values.email}
                    />

                </p>
            )}
                {touched.email && errors.email && <p className={"error"}> {errors.email}</p>}
                )}
                    <button
                        className={classe.footer_button_subscribe}
                        disabled={!isValid && !dirty}
                        onClick={handleSubmit}
                        type={"submit"}
                    >Subscribe</button>
        </div>



    )
}

export default FormSubscribe;
