import React from 'react'
import { useFormik} from 'formik';
import RegistrationSchema from './formValidationRules';
import * as Yup from 'yup'

const initialValues = {
    name: '',
    email: '',
    channel: ''
};

const onSubmit = values => {
    console.log('Form data', values)
};

const validate =  values => {

    let errors ={}

    if(!values.name){
        errors.name = 'Required'
    }

    if(!values.email){
        errors.email = 'Required'
    }

    if(!values.channel){
        errors.channel = 'Required'
    }

    return errors
}

const validationSchema = Yup.object({
    name: Yup.string().required('Required'),
    email: Yup.string().email('Invalid email format').required('Required'),
    channel: Yup.string().required('Required')
})


export default function SighnUpForm() {
    const formik = useFormik({
        initialValues,
        onSubmit,
        validationSchema
        // validate,
    })

    console.log('Visited fields', formik.touched)

    return(
        <div className="form__wrapper">
            <form onSubmit={formik.handleSubmit}>
                <div className="form__control">
                <label htmlFor="name">Name</label>
                <input type="text" id="name" name="name" onChange={formik.handleChange} onBlur={formik.handleBlur} value={formik.values.name}/>
                {formik.touched.name && formik.errors.name ? <div className="error">{formik.errors.name}</div> : null}
                </div>

                <div className="form__control">
                <label htmlFor="email">E-mail</label>
                <input type="email" id="email" name="email" onChange={formik.handleChange} onBlur={formik.handleBlur} value={formik.values.email}/>
                {formik.touched.email && formik.errors.email ? <div className="error">{formik.errors.email}</div> : null}
                </div>

                <div className="form__control">
                <label htmlFor="channel">Channel</label>
                <input type="text" id="channel" name="channel" onChange={formik.handleChange} onBlur={formik.handleBlur} value={formik.values.channel}/>
                {formik.touched.channel && formik.errors.channel ? <div className="error">{formik.errors.channel}</div> : null}
                </div>
                <button type="submit" className="form__btn">Submit</button>
            </form>
        </div>
)

}
