    email: '',
    password: '',
    confirmPassword: '',
  }

  const validationSchema = LoginSchema;

  return (
    <div>
      {/* <h1>LOGIN</h1>
      <div className="Form">
        <form onSubmit={formik.handleSubmit}>
          <label htmlFor="loginOrEmail">Email Address</label>
          <input
            id="email"
            name="loginOrEmail"
            type="email"
            onChange={formik.handleChange}
            onBlur={formik.handleBlur}
            value={formik.values.loginOrEmail}
          />
          {formik.touched.loginOrEmail && formik.errors.loginOrEmail ? (
            <div>{formik.errors.loginOrEmail}</div>
          ) : null}
          <label htmlFor="password">Password</label>
          <input
            id="password"
            name="password"
            type="password"
            onChange={formik.handleChange}
            onBlur={formik.handleBlur}
            value={formik.values.password}
          />
          {formik.touched.password && formik.errors.password ? (
            <div>{formik.errors.password}</div>
          ) : null}
          <button type="submit">Submit</button>
        </form>
      </div> */}
      <RegistrationForm initialValues={initialValues} validationSchema={validationSchema} onSubmit={onSubmit} />
      <div>
        <GoogleLogin
          clientId={configData.REACT_APP_GOOGLE_CLIENT_ID}
          buttonText="Login"
          onSuccess={responseSuccessGoogle}
          onFailure={responseErrorGoogle}
          cookiePolicy={"single_host_origin"}
        />
      </div>
      <h2> Welcome back {customerName(store)}</h2>
      <h2>{errorloginOrEmail(store)}</h2>
    </div>
  );
};

export default LoginPage;
